vector<char> floor(flor,flor+1975);


vector<int> chamber1;
vector<int> chamber2;
vector<int> chamber3;
vector<int> chamber4;
vector<int> chamber5;
vector<int> chamber[] = {chamber1, chamber2, chamber3, chamber4, chamber5};
static int track_point = 80;
bool out_of_tile = false;

void ultra_push(int chamber_index,int pos_index){
    chamber[chamber_index].push_back(pos_index);
    floor.at(pos_index) = '-';
}

void check_around(int c_index, int p_index){
    ultra_push(c_index,p_index);
    if (floor.at(p_index - 79) == '.') {
        check_around(c_index,p_index-79);
    }
    if (floor.at(p_index - 1) == '.') {
        check_around(c_index,p_index-1);
    }
    if (floor.at(p_index + 79) == '.') {
        check_around(c_index,p_index+79);
    }
    if (floor.at(p_index + 1) == '.') {
        check_around(c_index,p_index+1);
    }
    else{out_of_tile = true;}
}

